const mode = document.querySelector(".mode");
mode.addEventListener("click", () => {
  let e = document.body,
    o = document.querySelector(".container"),
    t = document.querySelector(".container-description"),
    c = document.querySelector(".add"),
    d = document.querySelector(".gallery"),
    s = document.querySelector("footer"),
    l = document.querySelector(".search > .input"),
    r = document.querySelector("span.mark");
  s.classList.toggle("bg-mode"),
    t.classList.toggle("dark-mode"),
    l.classList.toggle("bg-mode"),
    c.classList.toggle("dark-mode"),
    d.classList.toggle("dark-mode"),
    mode.classList.toggle("dark-mode"),
    e.classList.toggle("bg-mode"),
    o.classList.toggle("dark-mode"),
    r.classList.toggle("bg-mode");
});
